

var image = require('./lib/image.js');

var fs = require('fs');

var imgs = fs.readdirSync('./images');
var total = imgs.length;
var result = {};

function run(list) {
	var img = list.pop();
	if (!img) {
		console.log(result);
		console.log(total);
		console.log(Object.keys(result).length/total);
		process.exit(0);
	}
	image.imageGpsInfo('./images/' + img, function(err, res) {
		console.log(err);
		console.log(res);
		if (res) {
			result[img] = res;
		}
		run(list);
	});
}

run(imgs);
